package com.dayOneExe;

public class MultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int a =1; a<=6; a++) {
			for(int b =1 ; b<=6; b++) {
				System.out.println("******");
				System.out.println(a+ "x"+ b+ "="+ a*b);
				
			}
			
			
		}
	}

}
